            if self.container_var["General Settings"]["Calculation Accuracy"].get() == 1:
                elements_masses = {
                    "H": 1.008, "Li": 6.94, "C": 12.011, "O": 15.999, "Na": 22.99, "Mg": 24.305, "S": 32.06,
                    "Cl": 35.45, "K": 39.098, "Ca": 40.078, "Fe": 55.845}
