    def get_delimiter(self, var_line):
        n_commas = 0
        n_semicolons = 0

        n_commas += var_line.count(",")
        n_semicolons += var_line.count(";")

        if n_commas > 0 and n_semicolons == 0:
            delimiter = ","
        elif n_commas == 0 and n_semicolons > 0:
            delimiter = ";"

        return delimiter

