        # Build section 'Sample Files'
        var_sample_files = {"Row start": 17, "Column start": 19, "N rows": 18, "N columns": 26}
        self.place_sample_files_table(var_geometry_info=var_sample_files)
        # Build section 'Time-Signal Diagram Checker'
